import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';

pdfMake.vfs = pdfFonts.vfs;

const logoBase64 = 'data:image/png;base64,';

export type LROrder = {
  requestId: string;
  originCompany: string;
  consigneeName?: string;
  consigneeAddress?: string;
  pickupLocation?: string;
  deliveryLocation?: string;
  eWayBillNumber?: string;
  saidToContain?: string;
  packages?: number | string;
  weight?: number | string;
  cbm?: number | string;
  basicCharges?: number | string;
  pickupCharges?: number | string;
  deliveryCharges?: number | string;
  lrCharges?: number | string;
  handlingCharges?: number | string;
  taxAmount?: number | string;
  totalAmount?: number | string;
  customerInvNo?: string;
  goodsValue?: string;
  remarks?: string;
  date?: string;
  lrNumber?: string;
};

const defaultOrder = (o?: Partial<LROrder>): LROrder => ({
  requestId: o?.requestId || 'UNKNOWN',
  originCompany: o?.originCompany || 'Origin Company',
  consigneeName: o?.consigneeName || 'Consignee',
  consigneeAddress: o?.consigneeAddress || '',
  pickupLocation: o?.pickupLocation || '',
  deliveryLocation: o?.deliveryLocation || '',
  eWayBillNumber: o?.eWayBillNumber || 'XXXXXXXXXXXX',
  saidToContain: o?.saidToContain || 'GARMENTS',
  packages: o?.packages ?? '0',
  weight: o?.weight ?? '0.000',
  cbm: o?.cbm ?? '0.00',
  basicCharges: o?.basicCharges ?? '0.00',
  pickupCharges: o?.pickupCharges ?? '0.00',
  deliveryCharges: o?.deliveryCharges ?? '0.00',
  lrCharges: o?.lrCharges ?? '0.00',
  handlingCharges: o?.handlingCharges ?? '0.00',
  taxAmount: o?.taxAmount ?? '0.00',
  totalAmount: o?.totalAmount ?? '0.00',
  customerInvNo: o?.customerInvNo || '',
  goodsValue: o?.goodsValue || '',
  remarks: o?.remarks || '',
  date: o?.date || new Date().toLocaleDateString(),
  lrNumber: o?.lrNumber || '',
});

export const generateLRPdf = (orderPartial?: Partial<LROrder>) => {
  const order = defaultOrder(orderPartial);
  const copies = [
    'OFFICE COPY',
    'CONSIGNOR COPY',
    'CONSIGNEE COPY',
    'POD COPY',
  ];

  const contentPages = copies.map((copyTitle) => [
    {
      columns: [
        {
          width: 'auto',
          stack: [
            {
              image: logoBase64, width: 70, height: 45, alignment: 'left',
            },
          ],
        },
        {
          width: '*',
          stack: [
            {
              text: 'COLOMBUS FREIGHT LOGISTICS PVT LTD', style: 'title', alignment: 'center', color: '#008000',
            },
            {
              text: '(XPRESS PARCEL SERVICE)', style: 'subtitle', alignment: 'center', color: '#008000',
            },
          ],
        },
        {
          width: 'auto',
          stack: [
            {
              text: `LR NUMBER\n${order.lrNumber || ''}`, alignment: 'right', bold: true, fontSize: 10,
            },
            {
              text: `DATE: ${order.date}`, alignment: 'right', margin: [0, 4, 0, 0], fontSize: 9,
            },
          ],
        },
      ],
      margin: [0, 0, 0, 4],
    },
    {
      columns: [
        {
          width: '*',
          fontSize: 8,
          stack: [
            { text: 'Head Office : No.141-C, Iswarya Garden, Colombus Building,', bold: true },
            { text: 'Ammapalayam, Tiruppur-641 652. Tel: 73971-11865 Mobile : 94426-', margin: [0, 0, 0, 0] },
            { text: '02332 94421-19332 GSTIN: 33AADCC1547N2ZM' },
          ],
        },
        {
          width: '*',
          fontSize: 8,
          stack: [
            { text: 'Corporate Office : No.388/284, GLRS, Arian Street, Pallavaram,', bold: true, alignment: 'left' },
            { text: 'Chennai-600 043, Mobile: 94426-45332, 94422-79332', margin: [0, 0, 0, 0] },
          ],
        },
      ],
      margin: [0, 4, 0, 4],
    },
    // Consignor and Consignee
    {
      columns: [
        {
          width: '*',
          fontSize: 9,
          stack: [
            { text: 'Consignor Name & Address', bold: true },
            { text: order.originCompany, margin: [0, 2, 0, 0] },
            { text: 'E Land Fashion India P Ltd', italics: true }, // Example from image
            { text: 'Plot No 1, Netaji Apparel Park,NH 544 Ettiverampalayam, New,', margin: [0, 2, 0, 0] },
            { text: 'Tirupur, Tamil Nadu 641666' },
            { text: 'Mobile:9042824597, Email:', margin: [0, 2, 0, 0] },
          ],
        },
        {
          width: '*',
          fontSize: 9,
          stack: [
            { text: 'Consignee Name & Address', bold: true },
            { text: order.consigneeName, margin: [0, 2, 0, 0] },
            { text: order.consigneeAddress, margin: [0, 2, 0, 0] },
          ],
        },
      ],
      margin: [0, 4, 0, 4],
    },
    // From/To
    {
      columns: [
        {
          text: `FROM : ${order.pickupLocation || ''}`, bold: true, fontSize: 9, width: '*',
        },
        {
          text: `TO : ${order.deliveryLocation || ''}`, bold: true, fontSize: 9, width: '*',
        },
      ],
      margin: [0, 4, 0, 4],
    },
    // EWaybill, Payment Mode, Barcode, Copy Title
    {
      table: {
        widths: ['*', 'auto', 'auto', 'auto'],
        body: [
          [
            { text: `EWaybill Number : ${order.eWayBillNumber}`, fontSize: 9, bold: true },
            { text: 'Payment Mode\nCREDIT', fontSize: 9, alignment: 'center' },
            { text: '[Barcode Placeholder]', fontSize: 9, alignment: 'center' }, // Add real barcode if needed: { barcode: order.lrNumber, type: 'code128' }
            {
              text: copyTitle, bold: true, fontSize: 9, alignment: 'right',
            },
          ],
        ],
      },
      layout: 'lightHorizontalLines', // Added border
      margin: [0, 4, 0, 4],
    },
    // Description Table
    {
      table: {
        widths: ['auto', '*', 'auto', 'auto', 'auto'],
        body: [
          [
            { text: 'Description', bold: true, fontSize: 9 },
            { text: 'Said To Contain', bold: true, fontSize: 9 },
            { text: 'Pkgs', bold: true, fontSize: 9 },
            { text: 'Weight', bold: true, fontSize: 9 },
            { text: 'CBM', bold: true, fontSize: 9 },
          ],
          [
            'CARTON BOX',
            order.saidToContain,
            `${order.packages}`,
            `${order.weight}.000`,
            `${order.cbm}.000`,
          ],
          [
            { text: 'Total', bold: true, colSpan: 2 },
            {},
            `${order.packages}`,
            `${order.weight}.000`,
            `${order.cbm}.000`,
          ],
        ],
      },
      layout: 'lightHorizontalLines', // Added border
      margin: [0, 4, 0, 4],
    },
    // Charges Table (aligned right)
    {
      columns: [
        { width: '*', text: '' },
        {
          width: 200,
          table: {
            widths: ['*', 'auto'],
            body: [
              [{ text: 'Particulars', bold: true, fontSize: 9 }, {
                text: 'Amount', bold: true, fontSize: 9, alignment: 'right',
              }],
              ['BASIC CHARGES', { text: `${order.basicCharges}.00`, alignment: 'right' }],
              ['PICKUP CHARGES', { text: `${order.pickupCharges}.00`, alignment: 'right' }],
              ['DELIVERY CHARGES', { text: `${order.deliveryCharges}.00`, alignment: 'right' }],
              ['LR CHARGES', { text: `${order.lrCharges}.00`, alignment: 'right' }],
              ['HANDLING CHARGES', { text: `${order.handlingCharges}.00`, alignment: 'right' }],
              ['TAX AMOUNT', { text: `${order.taxAmount}.00`, alignment: 'right' }],
              [{ text: 'TOTAL AMOUNT', bold: true }, { text: `${order.totalAmount}.00`, bold: true, alignment: 'right' }],
            ],
          },
          layout: 'lightHorizontalLines', // Added border
        },
      ],
      margin: [0, 4, 0, 4],
    },
    // Customer Inv and Goods Value
    {
      table: {
        widths: ['*', '*'],
        body: [
          [{ text: `Customer Inv/B/E No : ${order.customerInvNo}`, fontSize: 9 }],
          [{ text: `Customer Goods Value : ${order.goodsValue}`, fontSize: 9 }],
        ],
      },
      layout: 'lightHorizontalLines', // Added border
      margin: [0, 4, 0, 4],
    },
    // Remarks
    {
      text: 'Remarks :',
      bold: true,
      fontSize: 9,
      margin: [0, 4, 0, 2],
    },
    // Terms & Conditions
    {
      columns: [
        {
          width: '*',
          fontSize: 8,
          stack: [
            { text: 'TERMS & CONDITIONS' },
            { text: '1. Jurisdiction Tirupur only.' },
            { text: '2. Claim will not be more than 1000/- for any Damage / Lose / Theft of parcels.' },
            { text: '3. Claim will be paid after 15 Days of Investigation.' },
            { text: '4. Please claim your booking within 24 hours of issued date otherwise per day demurrage charges will apply.' },
            { text: '5. Insurance should be claim by customer side for any damage or lose or theft of parcels.' },
          ],
        },
        {
          width: 'auto',
          fontSize: 8,
          stack: [
            { text: 'GOODS RECEIVED IN GOOD CONDITION', bold: true, alignment: 'center' },
            {
              text: 'FOR COLOMBUS FREIGHT PVT LTD.', bold: true, alignment: 'right', margin: [0, 20, 0, 0],
            },
            { text: 'Receiver : Sign, Mobile Number, Stamp, Date.', alignment: 'left', margin: [0, 20, 0, 0] },
          ],
        },
      ],
      margin: [0, 4, 0, 0],
    },
  ]);

  const content: any[] = [];
  contentPages.forEach((pageArr, idx) => {
    content.push(...pageArr);
    if (idx < copies.length - 1) {
      content.push({ text: '', pageBreak: 'after' });
    }
  });

  const docDefinition: any = {
    pageSize: 'A4',
    pageMargins: [20, 20, 20, 20],
    content,
    images: {
      logo: logoBase64,
    },
    styles: {
      title: { fontSize: 12, bold: true },
      subtitle: { fontSize: 8, italics: true },
    },
    defaultStyle: {
      fontSize: 9,
    },
  };

  const pdfObj = pdfMake.createPdf(docDefinition);
  return pdfObj;
};

